<?php//电影每日台词类 入口class DialogueAction extends CommonAction{		//封装多字段的搜索条件(加地址符直接修改原值)	public function _filter(&$map){			if(!empty($_REQUEST["keyword"])){			$where["en_dialogue"]=array("like","%{$_REQUEST['keyword']}%");			$where["cn_dialogue"]=array("like","%{$_REQUEST['keyword']}%");			$where["source"]=array("like","%{$_REQUEST['keyword']}%");			$where["id"]=array("like","%{$_REQUEST['keyword']}%");			$where["_logic"]="or";			$map["_complex"]=$where;		}				//状态的搜索		if(!empty($_REQUEST["status"])){			$map["status"]=$_REQUEST['status'];		}					}		//给添加的台词加上时间字段	public function insert(){		$_POST["addtime"]=time();		parent::insert();//调用父类中的添加方法进行添加	}		//修改修改时间	public function update(){		$_POST["addtime"]=time();		parent::update();	}		//修改状态表单	public function editstatus(){		$vo=M("Dialogue")->find($_GET["id"]);				$this->assign("vo",$vo);		$this->display("editstatus");	}			//修改状态操作	public function updatestatus(){			parent::update();	}			}