<?php//创建一个日志回复的关联model类class DiaryreplyModel extends RelationModel{	protected $_link=array(		//日志回复与回复者一对一		'User'=>array(			'mapping_type'=>BELONGS_TO,//关联类型 				'class_name'=>'User',//关联的模型类名				'foreign_key'=>'uid',//关联的外键名					'mapping_name'=>'user',//关联的映射名 默认是表名				'mapping_fields'=>'username,headpic', //要关联查询的字段				'as_fields'=>'username,headpic', //映射字段		),		);}